import { createContext, useContext, useReducer, useEffect } from "react";
import { useProductContext } from "./ContextProduct"
import reducer from "../reducer/filterReducers";
// import FilterContext from "../Context/filter_context.js";

const FilterContext = createContext();

const initialState = {
  filter_products: [],
  all_products: [],
  grid_view: true,
  sorting_value: "lowest",
};

export const FilterContextProvider = ({ children }) => {
  const { products } = useProductContext();

  const [state, dispatch] = useReducer(reducer, initialState);

  // to set the grid view
  const setGridView = () => {
    return dispatch({ type: "SET_GRID_VIEW" });
  };

  const setListView = () => {
    return dispatch({ type: "SET_List_VIEW" });
  };
    //  shoring function
     const sorting = (() => { 
       dispatch({type: "GET_SORT_VALUE"});
     });


     useEffect(() => {
      dispatch({ type: "SHORTING_PRODUCTS", payload: products });
    }, [state.sorting_value])

  useEffect(() => {
    dispatch({ type: "LOAD_FILTER_PRODUCTS", payload: products });
  }, [products]);

  return (
    <FilterContext.Provider
      value={{ ...state, setListView ,setGridView, sorting  }}>
      {children}
    </FilterContext.Provider>
  );
};

export const useFilterContext = () => {
  return useContext(FilterContext);
};
